import React from "react";
import {
	Box,
	IconButton,
	useBreakpointValue,
	Stack,
	Heading,
	Text,
	Container,
} from "@chakra-ui/react";

import Slider from "react-slick";
import { LeftOutlined, RightOutlined } from "@ant-design/icons";
import embed_dashboard_demo from "../../assets/features/embed_dashboard_demo.png";

// Settings for the slider
const settings = {
	dots: true,
	arrows: false,
	fade: true,
	infinite: true,
	autoplay: true,
	speed: 500,
	autoplaySpeed: 5000,
	slidesToShow: 1,
	slidesToScroll: 1,
};

export default function Features() {
	// As we have used custom buttons, we need a reference variable to
	// change the state
	const [slider, setSlider] = React.useState<Slider | null>(null);

	// These are the breakpoints which changes the position of the
	// buttons as the screen size changes
	const top = useBreakpointValue({ base: "90%", md: "50%" });
	const side = useBreakpointValue({ base: "30%", md: "40px" });

	// This list contains all the data for carousels
	// This can be static or loaded from a server
	const cards = [
		{
			title: "Feature 1",
			text: "Add your webhook API which will return the JSON data",
			image: "https://images.unsplash.com/photo-1516796181074-bf453fbfa3e6?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1yZWxhdGVkfDV8fHxlbnwwfHx8fA%3D%3D&auto=format&fit=crop&w=900&q=60",
		},
		{
			title: "Design Projects 2",
			text: "The project board is an exclusive resource for contract work. It's perfect for freelancers, agencies, and moonlighters.",
			image: "https://images.unsplash.com/photo-1438183972690-6d4658e3290e?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=2274&q=80",
		},
		{
			title: "Design Projects 3",
			text: "The project board is an exclusive resource for contract work. It's perfect for freelancers, agencies, and moonlighters.",
			image: embed_dashboard_demo,
		},
	];

	return (
		<Box
			position={"relative"}
			height={"600px"}
			width={"full"}
			overflow={"hidden"}
		>
			{/* CSS files for react-slick */}
			<link
				rel="stylesheet"
				type="text/css"
				charSet="UTF-8"
				href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick.min.css"
			/>
			<link
				rel="stylesheet"
				type="text/css"
				href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick-theme.min.css"
			/>
			{/* Left Icon */}
			<IconButton
				aria-label="left-arrow"
				variant="ghost"
				position="absolute"
				left={side}
				top={top}
				transform={"translate(0%, -50%)"}
				zIndex={2}
				onClick={() => slider?.slickPrev()}
			>
				<LeftOutlined />
			</IconButton>
			{/* Right Icon */}
			<IconButton
				aria-label="right-arrow"
				variant="ghost"
				position="absolute"
				right={side}
				top={top}
				transform={"translate(0%, -50%)"}
				zIndex={2}
				onClick={() => slider?.slickNext()}
			>
				<RightOutlined />
			</IconButton>
			{/* Slider */}
			<Slider {...settings} ref={(slider) => setSlider(slider)}>
				{cards.map((card, index) => (
					<Box
						key={index}
						height={"6xl"}
						position="relative"
						backgroundPosition="center"
						backgroundRepeat="no-repeat"
						backgroundSize="cover"
						backgroundImage={`url(${card.image})`}
					>
						{/* This is the block you need to change, to customize the caption */}
						<Container
							size="container.lg"
							height="600px"
							position="relative"
						>
							<Stack
								spacing={6}
								w={"full"}
								maxW={"lg"}
								position="absolute"
								top="50%"
								transform="translate(0, -50%)"
							>
								<Heading
									fontSize={{
										base: "3xl",
										md: "4xl",
										lg: "5xl",
									}}
								>
									{card.title}
								</Heading>
								<Text
									fontSize={{ base: "md", lg: "lg" }}
									color="GrayText"
								>
									{card.text}
								</Text>
							</Stack>
						</Container>
					</Box>
				))}
			</Slider>
		</Box>
	);
}
