import React from "react";
import {
	chakra,
	Box,
	Flex,
	useColorModeValue,
	SimpleGrid,
	GridItem,
	Heading,
	Text,
	Stack,
	FormLabel,
	Button as ChakraButton,
} from "@chakra-ui/react";
import { Form, Input, Button, Space } from "antd";
import { MinusCircleOutlined, PlusOutlined } from "@ant-design/icons";

export default function ClientDetails() {
	const onFinish = async () => {};
	return (
		<Form
			name="dynamic_form_nest_item"
			onFinish={onFinish}
			autoComplete="off"
		>
			<Box bg={useColorModeValue("gray.50", "inherit")} p={10}>
				<Box>
					<SimpleGrid
						display={{ base: "initial", md: "grid" }}
						columns={{ md: 3 }}
						spacing={{ md: 6 }}
					>
						<GridItem colSpan={{ md: 1 }}>
							<Box px={[4, 0]}>
								<Heading
									fontSize="lg"
									fontWeight="md"
									lineHeight="6"
								>
									Profile
								</Heading>
								<Text
									mt={1}
									fontSize="sm"
									color={useColorModeValue(
										"gray.600",
										"gray.400"
									)}
								></Text>
							</Box>
						</GridItem>
						<GridItem mt={[5, null, 0]} colSpan={{ md: 2 }}>
							<Stack
								px={4}
								py={5}
								bg={useColorModeValue("white", "gray.700")}
								spacing={4}
								p={{ sm: 6 }}
								style={{ minHeight: "88vh" }}
							>
								<FormLabel>Name</FormLabel>
								<Form.Item
									name="name"
									rules={[
										{
											required: true,
											message:
												"Please input the Client's name",
										},
									]}
								>
									<Input placeholder="Name" style={{width:"50%"}}/>
								</Form.Item>
								<>
									<FormLabel>Attribute</FormLabel>
									<Form.List name="users">
										{(fields, { add, remove }) => (
											<>
												{fields.map(
													({
														key,
														name,
														...restField
													}) => (
														<Space
															key={key}
															style={{
																display: "flex",
																marginBottom: 8,
															}}
															align="baseline"
														>
															<Form.Item
																{...restField}
																name={[
																	name,
																	"first",
																]}
																rules={[
																	{
																		required:
																			true,
																		message:
																			"Missing first name",
																	},
																]}
															>
																<Input placeholder="First Name" />
															</Form.Item>
															<Form.Item
																{...restField}
																name={[
																	name,
																	"last",
																]}
																rules={[
																	{
																		required:
																			true,
																		message:
																			"Missing last name",
																	},
																]}
															>
																<Input placeholder="Last Name" />
															</Form.Item>
															<MinusCircleOutlined
																onClick={() =>
																	remove(name)
																}
															/>
														</Space>
													)
												)}
												<Form.Item>
													<ChakraButton
														variant="outline"
														onClick={() => add()}
														leftIcon={
															<PlusOutlined color="blue.400" />
														}
													>
														<Text color="blue.400">
															Add Attribute
														</Text>
													</ChakraButton>
												</Form.Item>
											</>
										)}
									</Form.List>
								</>
								<Form.Item>
									<Button type="primary" htmlType="submit">
										Submit
									</Button>
								</Form.Item>
							</Stack>
						</GridItem>
					</SimpleGrid>
				</Box>
			</Box>
		</Form>
	);
}
