import {
  Box,
  Heading,
  Container,
  Text,
  Button,
  Stack,
  Icon,
  useColorModeValue,
  createIcon,
} from '@chakra-ui/react';
import "@fontsource/caveat"; 
import { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import { AuthContext } from '../../utils/AuthContext';
import { RoutePath } from '../../utils/routePath';

export default function Hero() {
  // Get current sign in User
	const user = useContext(AuthContext);
	let history = useHistory();
    return (
      <>
        <Container maxW={'3xl'} id="hero">
          <Stack
            as={Box}
            textAlign={'center'}
            spacing={{ base: 8, md: 14 }}
            py={{ base: 20, md: 36 }}>
            <Heading
              fontWeight={600}
              fontSize={{ base: '2xl', sm: '4xl', md: '6xl' }}
              lineHeight={'110%'}>
              Build analytic dashboard in<br />
              <Text as={'span'} color={'pink.400'}>
                3 minutes
              </Text>
            </Heading>
            <Text color={'gray.500'}>
              Design, arrange and implement the analytic dashboard in your system 
              without any single code. Let Codagence to help you to handle all the hassle
              by simplifing the development for you. 
            </Text>
            <Stack
              direction={'column'}
              spacing={3}
              align={'center'}
              alignSelf={'center'}
              position={'relative'}>
              <Button
                colorScheme={'green'}
                bg={'pink.400'}
                rounded={'full'}
                px={6}
                _hover={{
                  bg: '#4654A3',
                }}
                onClick={()=>{
                  if (user === null) {
                    // Proceed to login page
                    history.push(RoutePath.login);
                  } else {
                    // Go to email verification page if email not verified
                    !user?.emailVerified
                      ? history.push(RoutePath.email_verification)
                      : history.push(RoutePath.dashboard);
                  }
                }}>
                Get Started
              </Button>
              <Button variant={'link'} colorScheme={'blue'} size={'sm'}>
                Learn more
              </Button>
              <Box>
                <Icon
                  as={Arrow}
                  color={useColorModeValue('gray.800', 'gray.300')}
                  w={71}
                  position={'absolute'}
                  right={-71}
                  top={'10px'}
                />
                <Text
                  fontSize={'lg'}
                  fontFamily={'Caveat'}
                  position={'absolute'}
                  right={'-125px'}
                  top={'-15px'}
                  transform={'rotate(10deg)'}>
                  Starting at FREE
                </Text>
              </Box>
            </Stack>
          </Stack>
        </Container>
      </>
    );
  }

  const Arrow = createIcon({
    displayName: 'Arrow',
    viewBox: '0 0 72 24',
    path: (
      <path
