import {
	Box,
	Heading,
	Container,
	Text,
	Button,
	Stack,
	Icon,
	useColorModeValue,
	createIcon,
} from "@chakra-ui/react";
import "@fontsource/caveat";
import mixpanel from "mixpanel-browser";
import { useContext } from "react";
import { useHistory } from "react-router-dom";
import { AuthContext } from "../../utils/AuthContext";
import { RoutePath } from "../../utils/routePath";

export default function Hero() {
	// Get current sign in User
	const user = useContext(AuthContext);
	let history = useHistory();
	return (
		<>
			<Container maxW={"3xl"} id="hero">
				<Stack
					as={Box}
					textAlign={"center"}
					spacing={{ base: 8, md: 14 }}
					py={{ base: 20, md: 36 }}
				>
					<Heading
						fontWeight={600}
						fontSize={{ base: "2xl", sm: "4xl", md: "6xl" }}
						lineHeight={"110%"}
					>
						Better version of PowerBI
						<br />
						<Text as={"span"} color={"pink.400"}>
							Embedded Dashboard
						</Text>
					</Heading>
					<Text color={"gray.500"}>
						Design and embed the analytic dashboard to your site
						with high flexibility. You can change the parameter in
						the URL of the dashboard to show different
						visualisation.
					</Text>
					<Stack
						direction={"column"}
						spacing={3}
						align={"center"}
						alignSelf={"center"}
						position={"relative"}
					>
						<Button
							colorScheme={"green"}
							bg={"blue.400"}
							rounded={"full"}
							px={6}
							_hover={{
								bg: "#4654A3",
							}}
							onClick={async () => {
								mixpanel.track("Home_View_Dashboard_Click");

								if (user === null) {
									// Proceed to login page
									mixpanel.track("Home_View_Log_In_Proceed");
									history.push(RoutePath.login);
								} else {
									mixpanel.track(
										"Home_View_Dashboard_Proceed"
									);
									history.push(RoutePath.dashboard);
								}
							}}
						>
							Get Started
						</Button>
						<a
							href="https://www.producthunt.com/posts/codagence?utm_source=badge-featured&utm_medium=badge&utm_souce=badge-codagence"
							target="_blank"
							onClick={() => {
								mixpanel.track("Home_View_Producthunt_Click");
							}}
						>
							<img
								src="https://api.producthunt.com/widgets/embed-image/v1/featured.svg?post_id=342693&theme=light"
								alt="Codagence - No&#0045;code&#0032;analytic&#0032;dashboard&#0032;builder&#0032;in&#0032;3&#0032;minutes | Product Hunt"
								style={{ width: "250px", height: "54px" }}
								width="250"
								height="54"
							/>
						</a>

					</Stack>
				</Stack>
			</Container>
		</>
	);
}
