import { useEffect, useState } from "react";
import { AuthContext } from "./AuthContext";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "./configSetup";
import { RoutePath } from "./routePath";
import { useHistory } from "react-router-dom";
import { LocalStorage } from "./localStorage";

export const AuthProvider: React.FC = ({ children }) => {
	let history = useHistory();

	const [user, setUser] = useState<any>();

	useEffect(() => {
		const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
			let packagePlan = await LocalStorage.getPackagePlan();
			setUser(currentUser);
			if (currentUser) {
				history.replace(RoutePath.default);
			} else {
				console.log("here");

				if (
					currentUser!.uid !== undefined &&
					!currentUser!.emailVerified
				) {
					console.log("here 3");

					history.replace(RoutePath.email_verification);
				} else if (packagePlan === "free") {
					console.log("here 4");

					history.replace(RoutePath.product_choices);
				}
			}
		});

		return unsubscribe;
	}, []);

	return <AuthContext.Provider value={user}>{children}</AuthContext.Provider>;
};
