const ProtectedRoute = (props: any) => {
	return LocalStorage.getAuth() ? (
		LocalStorage.getEmailVerified() === "false" ? (
			<Redirect exact to={RoutePath.email_verification} />
		) : (
			<Route {...props} />
		)
	) : (
		<Redirect exact to={RoutePath.default} />
	);
};
