import {
	Button,
	Flex,
	Heading,
	Image,
	Stack,
	Text,
	useBreakpointValue,
} from "@chakra-ui/react";
import embed_dashboard_demo from "../../assets/features/embed_dashboard_demo.png";
import add_url_demo from "../../assets/features/add_url_demo.png";
import edit_data_demo from "../../assets/features/edit_data_demo.png";

export default function SplitScreen() {
	const content = [
		{
			title: "Enter Webhook API",
			desc: "Enter the API which will return teh data going to be visualised.",
			img: add_url_demo,
			highlight: "blue",
		},
		{
			title: "Edit Charts and Graphs",
			desc: "Edit and arrange the charts that are going to insert in dashboard.",
			img: edit_data_demo,
			highlight: "pink",
		},
		{
			title: "Embed the analytic dashboard",
			desc: "Embed the dashboard you have designed into your site.",
			img: embed_dashboard_demo,
			highlight: "yellow",
		},
	];
	return (
		<div>
			{content.map(({ title, desc, img, highlight }) => {
				return <Stack minH={"100vh"} direction={{ base: "column", md: "row" }}>
					<Flex p={8} flex={1} align={"center"} justify={"center"}>
						<Stack spacing={6} w={"full"} maxW={"lg"}>
							<Heading
								fontSize={{ base: "3xl", md: "4xl", lg: "5xl" }}
							>
								<Text
									as={"span"}
									position={"relative"}
									_after={{
										content: "''",
										width: "full",
										position: "absolute",
										bottom: 1,
										left: 0,
										bg: "blue.400",
										zIndex: -1,
									}}
								>
									Freelance
								</Text>
								<br />{" "}
								<Text color={"blue.400"} as={"span"}>
									Design Projects
								</Text>{" "}
							</Heading>
							<Text
								fontSize={{ base: "md", lg: "lg" }}
								color={"gray.500"}
							>
								The project board is an exclusive resource for
								contract work. It's perfect for freelancers,
								agencies, and moonlighters.
							</Text>
						</Stack>
					</Flex>
					<Flex flex={1}>
						<Image
							alt={"Add Image Url"}
							objectFit={"cover"}
							src={add_url_demo}
						/>
					</Flex>
				</Stack>;
			})}
		</div>
	);
}
