import React, { useContext, useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import _ from "lodash";
import {
	Button,
	Layout,
	Menu,
	Typography,
	Row,
	Col,
	Space,
	Divider,
	Card,
} from "antd";
import "./Home.less";
import homeHeaderImg from "../../assets/illustrations/home_header.png";
import use_case1 from "../../assets/illustrations/usecase_dashboard.png";
import use_case2 from "../../assets/illustrations/usecase_visualization_collection.png";
import use_case3 from "../../assets/illustrations/usecase_internal_sharing.png";
import use_case4 from "../../assets/illustrations/usecase_presentation.png";
import { RoutePath } from "../../utils/routePath";
import { AuthContext } from "../../utils/AuthContext";
import { LocalStorage } from "../../utils/localStorage";
import { FirebaseServices } from "../../utils/firebaseServices";
import { PricingCards } from "../../utils/components";
import { PaymentRepo } from "../../services/api/repositories/payment_repo";
import { UserRepo } from "../../services/api/repositories/user_repo";
import { User } from "../../services/api/models/user_model";
import Meta from "antd/lib/card/Meta";
import Nav from "./Nav";
import Hero from "./Hero";
import { Heading } from "@chakra-ui/react";

const { SubMenu } = Menu;
const { Header } = Layout;
const { Title } = Typography;

export const Home: React.FC<any> = () => {
	let userRepo = new UserRepo();

	let history = useHistory();

	// Get current sign in User
	const user = useContext(AuthContext);

	// Handle the state of each option in Menu bar
	const [_selectedKey, setSelectedKeys] = useState<string>("");

	const [_nickname, setNickname] = useState<string>("");

	useEffect(() => {
		async function initState() {
			let userId = await LocalStorage.getUserID();
			let apiResult = await userRepo.getUserById({
				userId: userId!,
			});
			if (apiResult.isSuccess) {
				let usermodel: User = apiResult.data[0];
				await LocalStorage.setEmail(usermodel?.email ?? "");
				await LocalStorage.setNickName(usermodel?.nickname ?? "");
				await LocalStorage.setPackagePlan(
					usermodel?.packagePlan ?? "free"
				);
			}
			// Set the state of nickname with the value retrieved from localstorage
			setNickname((await LocalStorage.getNickname()) ?? "");
		}

		initState();
	}, []);

	return (
		<Layout className="home_layout" style={{ overflow: "auto" }}>
			<Layout className="home_content">
				<Nav></Nav>
				<Hero></Hero>
				{/* Trusted By */}	
				{/* Use Case */}
				<div id="content_3" className="content_3">
				<Heading fontSize={'3xl'}>Features</Heading>


					<Title className="site_desc" level={4}>
						Use Codagence to connect the data from your API to Data
						Studio and other use cases:
					</Title>
					<Row className="use_case_row">
						{usecaseList.map((usecaseList) => (
							<Col
								xs={24}
								xl={6}
								className="use_case_col"
								span={6}
							>
								<Card
									cover={
										<img
											alt={usecaseList.title}
											src={usecaseList.image}
										/>
									}
								>
									<Meta
										style={{ height: "150px" }}
										title={usecaseList.title}
										description={usecaseList.desc}
									/>
								</Card>
							</Col>
						))}
					</Row>
				</div>
				{/* Pricing */}
				<div id="pricing" className="content_4">
					<Title
						className="site_desc"
						style={{ color: "#fff" }}
						level={1}
					>
						Pricing
					</Title>
					<PricingCards />
				</div>
				{/* Footer */}
				<div className="footer">
					<Space direction="vertical">
						<Title level={4}>Codagence</Title>
						<p>Codagence ©2021 Created by Codagence</p>
						<Row className="footer_row">
							<Col
								xs={24}
								xl={2}
								className="footer_col"
								span={2}
							></Col>
							<Col
								xs={24}
								xl={20}
								className="footer_col"
								span={20}
							>
								<Divider />
								<Row className="footer_nav_row">
									<Col
										xs={24}
										xl={6}
										className="footer_nav_col"
										span={6}
									>
										<a href="#Codagence">About</a>
									</Col>
									<Col
										xs={24}
										xl={6}
										className="footer_nav_col"
										span={6}
									>
										<a href="mailto:lucasgoh@codagence.com">
											Contact
										</a>
									</Col>
									<Col
										xs={24}
										xl={6}
										className="footer_nav_col"
										span={6}
									>
										<a
											href={
												"https://codagence.notion.site/TERMS-AND-CONDITIONS-416ed24afab14050a68e2f0d80357f65"
											}
										>
											Terms
										</a>
									</Col>
									<Col
										xs={24}
										xl={6}
										className="footer_nav_col"
										span={6}
									>
										<a
											href={
												"https://codagence.notion.site/Privacy-Policy-cda6ffd6fcb446868ccf63d0be5ea5b2"
											}
										>
											Privacy
										</a>
									</Col>
								</Row>
							</Col>
							<Col
								xs={24}
								xl={2}
								className="footer_col"
								span={2}
							></Col>
						</Row>
					</Space>
				</div>
			</Layout>
		</Layout>
	);
};

const usecaseList = [
	{
		image: use_case1,
		title: "Various data sources",
		desc: "Connect your data sources without worrying if it is supported by Data Studio",
	},
	{
		image: use_case2,
		title: "Manipulate data in scripts",
		desc: "Manipulate and clean your data in your scripts to overcome limited data transformation in Data Studio.",
	},
	{
		image: use_case3,
		title: "Internal sharing",
		desc: "Control data being retireved in the Data Studio according to the user's identity.",
	},
	{
		image: use_case4,
		title: "Familiar code environment",
		desc: "You can clean the data using the code base you familiar with.",
	},
];
