import React, { forwardRef, useCallback, useContext } from "react";
import {
	Button,
	Card,
	Col,
	Form,
	FormInstance,
	Input,
	PageHeader,
	Row,
	Space,
	Spin,
} from "antd";
import { useHistory } from "react-router-dom";
import Title from "antd/lib/typography/Title";
import "./components.less";
import { AuthContext } from "./AuthContext";
import { RoutePath } from "./routePath";
import { PaymentRepo } from "../services/api/repositories/payment_repo";
import PhoneInput from "react-phone-number-input";
import "react-phone-number-input/style.css";
import { useState } from "react";
import { LocalStorage } from "./localStorage";
import { Response } from "../services/response";
import { FirebaseServices } from "./firebaseServices";
import {
	Box,
	FormLabel,
	GridItem,
	Heading,
	SimpleGrid,
	Stack,
	useColorModeValue,
	Button as ChakraButton,
	Text,
} from "@chakra-ui/react";
import { MinusCircleOutlined, PlusOutlined } from "@ant-design/icons";

interface ComponentProps {
	title?: string;
	isLoading?: boolean; // To identify if the component is in loading state
	enabled?: boolean; // To identify if the component is enabled or some field of the component is enabled
}

export const BackHeader: React.FC<ComponentProps> = ({ title }) => {
	let history = useHistory();
	function goBack() {
		// Navigate to previous page
		history.goBack();
	}
	return (
		<PageHeader
			className="site-page-header"
			ghost={false}
			onBack={() => goBack()}
			title={title}
		/>
	);
};

export const EmailField: React.FC<ComponentProps> = ({}) => {
	return (
		<>
			<FormLabel>Email</FormLabel>{" "}
			<Form.Item
				name="email"
				rules={[
					{
						type: "email",
						message: "The input is not valid E-mail!",
					},
					{
						required: true,
						message: "Please input your E-mail!",
					},
				]}
			>
				<Input type="email" style={{ width: "100%" }} />
			</Form.Item>
		</>
	);
};

export const PasswordField: React.FC<ComponentProps> = ({ enabled }) => {
	//If true, then the min 6 validator is enabled
	return (
		<>
			<FormLabel>Password</FormLabel>
			<Form.Item
				name="password"
				rules={
					enabled
						? [
								{
									required: true,
									message: "Please input your password!",
								},
								{
									min: 6,
									message:
										"Password must be minimum 6 characters.",
								},
						  ]
						: [
								{
									required: true,
									message: "Please input your password!",
								},
						  ]
				}
				hasFeedback
			>
				<Input.Password style={{ width: "100%" }} />
			</Form.Item>
		</>
	);
};

export const ConfirmPasswordField: React.FC<ComponentProps> = ({}) => {
	return (
		<>
			<FormLabel>Confirm Password</FormLabel>
			<Form.Item
				name="confirm"
				dependencies={["password"]}
				hasFeedback
				rules={[
					{
						required: true,
						message: "Please confirm your password!",
					},
					({ getFieldValue }) => ({
						validator(_, value) {
							if (!value || getFieldValue("password") === value) {
								return Promise.resolve();
							}
							return Promise.reject(
								new Error(
									"The two passwords that you entered do not match!"
								)
							);
						},
					}),
				]}
			>
				<Input.Password style={{ width: "100%" }} />
			</Form.Item>
