import {
	Flex,
	Stack,
	Heading,
	Text,
	Input,
	Button,
	Icon,
	useColorModeValue,
	createIcon,
} from "@chakra-ui/react";
import { Form, message } from "antd";
import { useRef } from "react";
import { NewsletterRepo } from "../../services/api/repositories/newsletter_repo";

export default function CardWithIllustration() {
	let newsletterRepo = new NewsletterRepo();


	const subscribeNewsletter = async (values: any) => {
		console.log(values['email']);
		// let apiResult = await newsletterRepo.subscribeNewsletter({ email: inputEmail.current });

		// if(apiResult.isSuccess){
		//     message.success("You have successfully subscibed");
		// } else {
		//     message.error("Fail to subscribe");
		// }
	};
	return (
		<Flex
			minH={"100vh"}
			align={"center"}
			justify={"center"}
			py={12}
			bg={useColorModeValue("gray.50", "gray.800")}
		>
			<Stack
				boxShadow={"2xl"}
				bg={useColorModeValue("white", "gray.700")}
				rounded={"xl"}
				p={10}
				spacing={8}
				align={"center"}
			>
				<Icon as={NotificationIcon} w={24} h={24} />
				<Stack align={"center"} spacing={2}>
					<Heading
						textTransform={"uppercase"}
						fontSize={"3xl"}
						color={useColorModeValue("gray.800", "gray.200")}
					>
						Subscribe
					</Heading>
					<Text fontSize={"lg"} color={"gray.500"}>
						Subscribe to our newsletter & stay up to date!
					</Text>
				</Stack>
				<Stack
					spacing={4}
					direction={{ base: "column", md: "row" }}
					w={"full"}
				>
					<Form
						name="register"
						className="register_form"
						initialValues={{ remember: true }}
						onFinish={subscribeNewsletter}
					>
						<Form.Item
							name="email"
							rules={[
								{
									type: "email",
									message: "The input is not valid E-mail!",
								},
								{
									required: true,
									message: "Please input your E-mail!",
								},
							]}
						>
							<Input
								type={"text"}
								placeholder={"abc@codagence.com"}
								color={useColorModeValue(
									"gray.800",
									"gray.200"
								)}
								bg={useColorModeValue("gray.100", "gray.600")}
								rounded={"full"}
								border={0}
								_focus={{
									bg: useColorModeValue(
										"gray.200",
										"gray.800"
									),
									outline: "none",
								}}
							/>
						</Form.Item>
						<Form.Item>
							<Button
								bg={"blue.400"}
								rounded={"full"}
								color={"white"}
								flex={"1 0 auto"}
								_hover={{ bg: "blue.500" }}
								_focus={{ bg: "blue.500" }}
								onClick={() => subscribeNewsletter}
							>
								Subscribe
							</Button>
						</Form.Item>
					</Form>
				</Stack>
			</Stack>
		</Flex>
	);
}

